// TODO:

// Do we need this or can we just add the reviews information to the initial request?

import { useEffect, useState } from "react";

const Reviews = (restaurantId) => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    getReviews();
  }, []);

  const getReviews = async () => {
    console.log(restaurantId);
    const response = await fetch(
      `http://localhost:8080/api/eateries/${restaurantId}/reviews`
    );
    const json = response.json();
    setReviews(json);
  };

  console.log(reviews);
  return (
    <ul>
      <li>
        {reviews.map((review) => (
          <li>{review}</li>
        ))}
      </li>
    </ul>
  );
};

export default Reviews;
