import Layout from "../../components/Layout";

const Restaurant = ({ name }) => {
  console.log(name);
  return (
    <Layout>
      <h1>{name}</h1>
    </Layout>
  );
};

export async function getStaticPaths() {
  // Call an external API endpoint to get restaurants
  const response = await fetch("http://localhost:8080/api/eateries");
  const restaurants = await response.json();

  // Get the paths we want to pre-render based on restaurants
  const paths = restaurants.data.map(
    (restaurant) => `/restaurants/${restaurant.id}`
  );

  // We'll pre-render only these paths at build time.
  // { fallback: false } means other routes should 404.
  return { paths, fallback: false };
}

export async function getStaticProps({ params }) {
  const response = await fetch(
    `http://localhost:8080/api/eateries/${params.id}`
  );
  const json = await response.json();

  return {
    props: json.data.attributes,
  };
}

export default Restaurant;
